import express from "express";
import morgan from "morgan"
import user from "./routes/producto.routes";
import cors from "cors";

const app = express();

//Settings
app.set("port",4000);

//Middlewares
app.use(morgan("dev"));
app.use(express.json());
app.use(cors());

//Routes
app.use(user);

export default app;

